const light = msg.light;
const room = msg.room;
const type = msg.type;
const light_scene_import = msg.light_scene;

const light_scenes = global.get("light_scene");
const standard_scenes = Object.values(light_scenes.standard);
const custom_scenes = Object.values(light_scenes.custom);
const hue_scenes = Object.values(light_scenes.hue);
const falcon_scenes = Object.values(light_scenes.falcon);

var new_light_scene = light_scene_import
var options = ["--Select--"];

if (light_scene_import == "Off" || light_scene_import == "Turn Off") {
	options.push("Off");
	new_light_scene = "Off";
}
if (light_scene_import == "Random" || light_scene_import == "Shuffle") {
	options.push("Random");
	new_light_scene = "Random";
}
if (type == "on_off" || type == "general" ) {
	if (light_scene_import == "Off" || light_scene_import == "Turn Off") {
	} else if (light_scene_import == "On" || light_scene_import == "Turn On") {
		options.push("On");
		new_light_scene = "On";
	} else {
		new_light_scene = "--Select--";
	}
} else {	
	if (light_scene_import == "Undefined" || light_scene_import == "--Select--") {
		options.push("Undefined");
		new_light_scene = "Undefined";
	}
}
options.push("Turn Off");
if (new_light_scene == "Off" ) {
	options.push("Turn On");
}
if (type != "on_off") {
	standard_scenes.forEach((scene) => options.push(scene));
	if (type != "basic" ) {
		options.push("Shuffle");
	}
	if ( type == "general" || type == "group" || type == "hue" || type == "espixel" ) {
		options.push("Spectrum");
	}
	if ( type == "general" || type == "group" || type == "hue" || type == "espixel"  || light == "light.bedroom_television_backlight" ) {
		hue_scenes.forEach((scene) => options.push(scene));
	}
	if (room == "Patio") {
		falcon_scenes.forEach((scene) => options.push(scene));
	}
}

msg.light_scene = new_light_scene;
msg.payload = {
    data: {options}
};

return msg;
